#!/usr/bin/env python3
"""
Simple mock API server to test CORS
"""
from fastapi import FastAPI
from fastapi.middleware.cors import CORSMiddleware
import uvicorn

app = FastAPI(title="CV-JD Mock API")

# Add CORS middleware
app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],  # Allow all origins for testing
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

@app.get("/health")
def health_check():
    return {"status": "healthy", "message": "Mock API is running"}

@app.get("/")
def root():
    return {"message": "CV-JD Mock API", "status": "running"}

@app.get("/api/v1/cv/")
def list_cvs():
    return []

@app.get("/api/v1/jd/") 
def list_jds():
    return []

if __name__ == "__main__":
    print("Starting mock API server on http://localhost:8080")
    print("CORS is enabled for all origins")
    uvicorn.run(app, host="0.0.0.0", port=8080)
